mapper.update:
update boards set 
	  board_title    = #{board_title}
	, board_content  = #{board_content}
	, board_category_number = #{board_category_number}
	, board_last_updated = NOW()
where board_number = #{board_number}

update boards set 
	 board_viewcnt    = #{board_viewcnt}
where board_number = #{board_number}
